import { MembershipSource } from './membership-source.js';
import { Role } from './role.js';
import './membership-source-types.js';
import './membership-types.js';

type Account<P = any> = {
    readonly id: string;
    readonly aadObjectId?: string;
    readonly role: Role;
    readonly name: string;
    readonly properties?: P;
    readonly membershipSources?: MembershipSource[];
};
type ConversationAccount = {
    readonly id: string;
    readonly tenantId?: string;
    readonly conversationType: 'personal' | 'groupChat' | Omit<string, 'personal' | 'groupChat'>;
    readonly name?: string;
    readonly isGroup?: boolean;
};

export type { Account, ConversationAccount };
